import React, {useState} from "react";
import { useSelector, useDispatch } from "react-redux";
import { dialogActions } from "../../store/modalAndAlerts";
import ModalReg from "../UI/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faUserCircle } from "@fortawesome/free-solid-svg-icons";
import LabelLayout from "./LabelLayout";
import "./UserProfileLayout.css";
import mosaic from "../../assets/Images/mosaic.jpg";
import UploadIcon from "./UploadIcon";

const UserProfileLayout = () => {
  const [showDropdown ,setShowDropdown] = useState(false)

  const dispatch = useDispatch();
  useSelector(state => state.dialog.modalToggle);

  const handleChangeProfile = () => {
      dispatch(dialogActions.modalToggle())
    return () => {};
  };

  const handleViewProfile = () => {
    dispatch(dialogActions.modalToggle())
  }


  const handleDropdown = () => {
    setShowDropdown((prevDropdown) => !prevDropdown)
  }

  return (
    <div><ModalReg
        title="Change profile picture"
        customStyle={{
          width: "400px",
          height: "300px",
          marginLeft: "40%",
          marginTop: "40px",
        }}
      >
        {" "}
        <UploadIcon />{" "}
      </ModalReg>
      <div className="profile-main">
        <div className="profile-header">
          <img src={mosaic} alt="cover" />
          <div className="d-flex justify-content-center">
            <div id="trapezoid"></div>
          </div>
          <div className="user-detail">
            <div className="footer">
              <div className="footer-box">
                <i className="fa fa-facebook"></i>
              </div>
              <div className="footer-box">
                <i className="fa fa-twitter"></i>
              </div>
              <div className="footer-box">
                <i className="fa fa-linkedin"></i>
              </div>
              <img
                src="https://img.icons8.com/ios-filled/48/000000/tiktok--v1.png"
                alt="tiktokIcon"
              />

              <div className="footer-box">
                <i className="fa fa-instagram"></i>
              </div>
            </div>

            <div className="user-image">
              <img src="http://storage0.dms.mpinteractiv.ro/media/1/1481/21335/14078774/1/federer-nou-ap.jpg" alt="..." />
              <div className="after" onClick={handleDropdown}>
                <FontAwesomeIcon icon={faEdit} />
              </div>
            </div>
            {showDropdown &&
              <div className="box2 sb10">
                <div className='listDropdown' onClick={handleViewProfile}><FontAwesomeIcon icon={faUserCircle} style={{'float':'left', 'fontSize':'22px'}}/><span style={{}}>View profile picture</span></div>
                <div className='listDropdown' onClick={handleChangeProfile}><FontAwesomeIcon icon={faEdit} style={{'float':'left', 'fontSize':'22px'}}/><span style={{}}>Change profile picture</span></div>
            </div>}

            <h2>Smith Alexander</h2>
            <div className="post-label">
              <span>Animal Lover</span>
              <span>Sport</span>
              <span>Cooking</span>
            </div>
            <div className="job__title">
              Founder and CEO at <strong>NewSpot</strong>
            </div>
            <p>
              <i className="fa fa-map-marker" aria-hidden="true"></i>
              <span className="city__label">Iasi</span>
            </p>
            <div className="rating__label">Rating: 99%</div>
            <div className="bio-box">
              <p>Bio</p>
              <div className="desc">
                Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
                enim ad minim veniam.
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <div className="sport-labels">
          <LabelLayout />
        </div>
      </div>
    </div>
  );
};

export default UserProfileLayout;
