import React, {useState} from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { dialogActions } from '../../store/modalAndAlerts'
import "./UploadIcon.css";

const UploadIcon = () => {
  const [isLoading, setIsLoading] = useState(false)
  const [selectedFile, setSelectedFile] = useState()
  const [responseOk, setResponseOk] = useState(false)
  const [preview, setPreview] = ('')

  const dispatch = useDispatch()
  useSelector((state) => state.dialog.modalToggle)

  const token = useSelector((state) => state.authentication.token)

  const handleModalClose = () => {
    dispatch(dialogActions.modalToggle())
  }

  const handleImage = (event) => {
		setSelectedFile(event.target.files[0]);
	};

  const handlePreview = async (event) => {
    setIsLoading(true);

    const formData = new FormData();
		formData.append('profile', selectedFile);

    console.log(formData, 'formdata from fetch')

    try {
      const response = await fetch('http://localhost:5000/uploadimage',
        {
          method: 'POST',
          headers:{'Authorization': "Bearer " + token},
          body: formData,
        }
      )
      const responseData = await response.json()
      if (response.ok) {
        console.log("response.OK from /userimage > responnseData :",responseData);
        // isSuccess used to show message
        setResponseOk(true)
        const objectUrl = URL.createObjectURL(selectedFile)
        setPreview(objectUrl)
      } else {
        setIsLoading(false);
        return new Error(responseData.message);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
    }
    setIsLoading(false)
  };

  const handleUpload = async (e) => {
    
    const responseFinal = await fetch('http://localhost:5000/uploadimage',
    {
      method: 'POST',
      headers:{'Authorization': "Bearer " + token},
      body: 'responseYES'
    })
    const responseDataFinal = await responseFinal.json()
    if(responseFinal.ok){
      console.log('response Upload', responseDataFinal)
      
    }else{
      console.log('error', responseDataFinal)
    }
    setResponseOk(false)
    setPreview(null)
  }
	
  return (
    <React.Fragment>

      {!responseOk && <form encType="multipart/form-data">
        {isLoading && <div>Please don't close this window while we processing your request</div>}
        <div className="wrapper"  >
          <div className="file-upload">
            <input type="file" id="fileUpload" name="profile" onChange={handleImage}/>
            <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
          </div>
        </div>
        {isLoading && <Spinner animation="border" variant="primary"/>}
        <Button onClick={handlePreview} style={{borderColor:'transparent'}} disabled={isLoading}>Preview</Button>
      </form>}
      {responseOk && <form>
        <img src={preview} alt="errorIMG"/>
        <Button onClick={handleModalClose} style={{borderColor:'transparent'}}>Close</Button>
        <Button onClick={handleUpload} style={{borderColor:'transparent'}} disabled={isLoading}>Upload</Button>
      </form>}
    </React.Fragment>
  );
};
// const objectUrl = URL.createObjectURL(selectedFile)

export default UploadIcon;
