import React, { useContext } from "react";
import { Navbar, Nav, Container, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { AuthContext } from '../../shared/context/auth-context'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import "./HeaderNav.css";

const HeaderNav = () => {
  const auth = useContext(AuthContext);

  const handleLogout = () => {
    auth.logout()
  }
  
  const modalHandler = () => {
    auth.modalShow()
  }


  return (
    <Navbar sticky="top" id="navbar">
      <Container>
        <LinkContainer to="/events">
          <Navbar.Brand>Sport Together</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">

          <Nav className="me-auto">
            <LinkContainer to="/events">
              <Nav.Link >Events</Nav.Link>
            </LinkContainer>
          </Nav>
          
         
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNav;


<Navbar bg="light" expand="lg">
  <Container>
    <Navbar.Brand href="#home"><LinkContainer to="/events">
          <Navbar.Brand>Sport Together</Navbar.Brand>
        </LinkContainer>
    </Navbar.Brand>
    <Navbar.Toggle aria-controls="basic-navbar-nav" />
    <Navbar.Collapse id="basic-navbar-nav">
      <Nav className="me-auto">
        <Nav.Link href="#home">Home</Nav.Link>
        <Nav.Link href="#link">Link</Nav.Link>
        <NavDropdown title="Account" id="basic-nav-dropdown">
              <div className="main-wrapper__profile">
                <div className="badge1 custom">
                  <div className="circle"><FontAwesomeIcon icon={faUserCircle}/></div>
                </div>
              </div>  
              <NavDropdown.Item ><Link to="/userprofile"> Profile </Link></NavDropdown.Item>
              <NavDropdown.Item>
                <Nav>
                  {auth.isLogged && <Nav.Link onClick={handleLogout}>Logout</Nav.Link>}
                  {!auth.isLogged && <Nav.Link variant="primary" onClick={modalHandler}> Register!!</Nav.Link>}
                </Nav>
              </NavDropdown.Item>
          </NavDropdown>
      </Nav>
    </Navbar.Collapse>
  </Container>
</Navbar>
