import React from "react";
import EventItem from "./EventItem";

const EventList = (props) => {
    
    let arrayEvents = [...props.eventsList]
    if(props.allFilters.sportFilter !== '') {
        arrayEvents = arrayEvents.filter(item => item.Sport === props.allFilters.sportFilter);      
        }
    
    if(props.allFilters.difficultyFilter !== '') {
        arrayEvents = arrayEvents.filter(item => item.Level === props.allFilters.difficultyFilter);       
    }

    return (
        <div>
            {arrayEvents.map((item) => (
            <EventItem key = {item.id}
                        sport = {item.Sport}
                        level = {item.Level}
                        city = {item.County}
                        location = {item.ExactLocation}
                        description = {item.Description}
                        date = {item.Date}
                        time = {item.Time}
                        nrPlayers = {item.NrPlayers}
                        allowed = {item.LevelRequirement}
            /> 
            ))}
        </div>
    )
}

export default EventList;

