const Events = () => {
  const [showForm, setShowForm] = useState(false);
  const [filters, setFilters] = useState({sportFilter:'', difficultyFilter: ''})
  const [allEvents, setAllEvents] = useState('')
  
  const auth = useContext()

  useEffect(() => {
    const fetchMyAPI = async() => {
      let response = await fetch('http://localhost:5000/events', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '+auth.token,
        }
      })
      const responseData = await response.json()
      console.log('responseData: ',responseData)
      setAllEvents(responseData)
      
    } 
    
    fetchMyAPI()
  },[])

  const showEventFormHandler = (formBoolean) => {
    setShowForm(formBoolean);
  };
  const addEventItemHandler = (newEvent) => {
    setShowForm(newEvent)
  }

  const getSportFilter = (sportFilter) => {
    // console.log(sportFilter)
    setFilters({sportFilter: sportFilter, difficultyFilter: filters.difficultyFilter})
  }
  const getDifficultyFilter = (difficultyFilter) => {
    // console.log(difficultyFilter)
    setFilters({sportFilter: filters.sportFilter, difficultyFilter: difficultyFilter})
  }




  return (
    <div className="body">
      <Container className="justify-content-center">
        <Row id="search" className="justify-content-center">
          <Col xl={3}>
            <UserProfileShowPage />
            <CreateEventLayout onShowEventForm={showEventFormHandler} onShowAddEvent={showForm}/>
          </Col>
          <Col xl={7}>
            {showForm && <AddEventForm onAddEventItem = {addEventItemHandler}/>}
            {!showForm && <SearchForm />}
            <EventList eventsList = {allEvents} allFilters={filters}/>
          </Col>
          <Col xl={2}>
            {!showForm && <FilterEvents onAddSportFilter={getSportFilter}  onAddDifficultyFilter={getDifficultyFilter}/>}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Events;
