import React from "react";
import './Homepage.css'
import Modal from "../components/UI/Modal";
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from "../components/Forms/RegisterForm";
import tennisImg from "../assets/Images/tennis-front.jpg"

const Homepage = (props) => {
  const [modalShow, setModalShow] = useState(false);


   

      <Modal show={modalShow} onHide={() => setModalShow(false)}/>
 


  return (
    <React.Fragment>
      <div><img id='homepageImg' src={tennisImg} alt="..."/> </div>
      <Container >
          <Row>
            {props.registerOrLogin && <Col xl={8}><LoginForm /></Col>}
            {!props.registerOrLogin && <Col xl={8}><RegisterForm /></Col>}
            <Col xl={{span:4, order:'first'}}><CarouselHomepage/></Col>
          </Row>
          <Row>
            <Col xl={12} style={{'position':'relative'}}>
              
            </Col>
          </Row>
      </Container>  
    </React.Fragment>
  );
};

export default Homepage;
