import React, { useState } from "react";
import './Homepage.css'
import HeaderNav from "../components/Layout/HeaderNav";
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from "../components/Forms/RegisterForm";
import Image from "../assets/Images/checkingWatch.png"
const Homepage = (props) => {
  const [form, setForm] = useState(true);
  const [isLogged, setIsLogged] = useState(false)
  const formHandler = (data) => {
    setForm(data);
    console.log(form);
  };

  
  return (
    <React.Fragment>
      <HeaderNav onShowForm={formHandler} isLogged={(getResponse) => setIsLogged()}/>
        <div><img id='logo' src={Image} alt="..."/> </div>
        <div id="body1">
        <Container >
            <Row>
              
              {form && <Col xl={8}><LoginForm getResponse={onGetResponse}/></Col>}
              {!form && <Col xl={8}><RegisterForm /></Col>}
              <Col xl={{span:4, order:'first'}}><CarouselHomepage/></Col>
            </Row>
            <Row>
              <Col xl={12} style={{'position':'relative'}}>
                
              </Col>
            </Row>
        </Container>  
        </div>
    </React.Fragment>
  );
};

export default Homepage;
