import React, { useState } from "react";
import { Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { alertActions } from "../../store/alert";

const AlertDismissible = (props) => {
  const dispatch = useDispatch;
  const alertActive = useSelector(state => state.alert.alertToggle)
  const alertVariant = useSelector((state) => state.alert.alertVariant)
  const alertTitle = useSelector((state) => state.alert.alertTitle)
  const alertBody = useSelector((state) => state.alert.alertBody)

  const [show, setShow] = useState(true)

  const handleClose = () => {

    dispatch(alertActions.alertToggle())
    setShow(false)
  }

  return (
    <Alert variant={alertVariant} onClose={handleClose} dismissible>
    <Alert.Heading className="center">{alertTitle}</Alert.Heading>
    {alertBody && <p>{alertBody}</p>}
  </Alert>
  );
}
