import React from "react";
import { Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";

const AlertDismissible = (props) => {
  const alertVariant = useSelector((state) => state.alert.alertVariant)
  const alertTitle = useSelector((state) => state.alert.alertTitle)
  const alertBody = useSelector((state) => state.alert.alertBody)


  return (
    <Alert variant={alertVariant}>
      <Alert.Heading className="center">{alertTitle} <FontAwesomeIcon icon={faTimes} /></Alert.Heading>
      <div>{alertBody}</div>
    </Alert>
  );
}
