import { createSlice } from "@reduxjs/toolkit"

const initialDialogState = {
    modalToggle:false,
    modalAlertToggle:false,
    modalTitle: null,
    subModalToggle: false,
    subModalTitle:null
}

const dialogSlice = createSlice({
    name:'dialog',
    initialState: initialDialogState,
    reducers: {
        modalToggle(state) {
            state.modalToggle = !state.modalToggle;
        },
        modalAlertToggle(state){
            state.modalAlertToggle = !state.modalAlertToggle
        },
        modalTitle(state, action) {
            state.modalTitle = action.payload
        },
        subModalToggle(state){
            state.subModalToggle = !state.subModalToggle
        },
        subModalTitle(state){
            state.subModalTitle = !state.subModalTitle
        }
    }
})

export const dialogActions = dialogSlice.actions;

export default dialogSlice.reducer;
