import React from "react";
import './MyEvents.css';
import UserProfileCard  from '../components/UserProfile/UserProfileCard';

const MyEvents = () => {
    const [showForm, setShowForm] = useState(false);
    const [allEvents, setAllEvents] = useState('')
  
    const auth = useContext(AuthContext)
  
    useEffect(() => {
      const fetchMyAPI = async() => {
        let response = await fetch('http://localhost:5000/events', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer '+auth.token,
          }
        })
        const responseData = await response.json()
        console.log('responseData: ',responseData)
        setAllEvents(responseData)
      }   
      fetchMyAPI()
    }, [auth.token])
  
    const showEventFormHandler = (formBoolean) => {
      setShowForm(formBoolean);
    };
    const addEventItemHandler = (newEvent) => {
      setShowForm(newEvent)
    }
  
    
    return(
        <div className="body">
            <Container className="justify-content-center">
                <Row id="search" >
                <Col sm={3}>
                    <UserProfileCard />
                    <CreateEventLayout onShowEventForm={showEventFormHandler} onShowAddEvent={showForm}/>
                </Col>
                <Col md={7}>
                    {showForm && <AddEventForm onAddEventItem = {addEventItemHandler}/>}
                    {!showForm && <SearchForm />}
                    <EventList eventsList = {allEvents}/>
                </Col>
                <Col xl={2}>
                    Here Something? ??? ? 
                </Col>
                </Row>
            </Container>
        </div>
    )
}

export default MyEvents;
